import React, { useState } from "react";
import { Button } from "carbon-components-react";
import { ContentSwitcher } from "carbon-components-react";
import { Switch } from "carbon-components-react";
// import { Grid, Row, Column } from "carbon-components-react";
import Calidad from "./tabs/Calidad";
import Produccion from "./tabs/Produccion";
import Pruebas from "./tabs/Pruebas";
import TecnicalTest from "./tabs/Manufactura";
import Estatus from "./tabs/Estatus";

function About() {
  const [nombre, setNombre] = useState(<Produccion />);

  const handleProduccion = () => {
    setNombre(<Produccion />);
  };
  const handleCalidad = () => {
    setNombre(<Calidad />);
  };
  const handlePruebas = () => {
    setNombre(<Pruebas />);
  };
  const handleManufactura = () => {
    setNombre(<TecnicalTest />);
  };
  const handleEstatus = () => {
    setNombre(<Estatus />);
  };

  function changeBackground(e) {
    e.target.style.background = "#0093E9";
  }
  function changeBackground2(e) {
    e.target.style.background = "#fff";
  }

  return (
    <div
      style={{
        backgroundColor: "#fff",
        // height: "950px",
        color: "white",
        textAlign: "center"
      }}
    >
      <title>Forms Ess</title>
      <link rel="preconnect" href="https://fonts.googleapis.com" />
      <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin />
      <link
        href="https://fonts.googleapis.com/css2?family=Dancing+Script&display=swap"
        rel="stylesheet"
      />
      <br />
      <br />

      {/* <Button onClick={handleProduccion}   >Produccion</Button>
      <Button onClick={handleCalidad}   >Calidad</Button>
      <Button onClick={handlePruebas}  >Pruebas</Button> */}
      <Button
        kind="ghost"
        onMouseOver={changeBackground}
        onMouseOut={changeBackground2}
        onClick={handleProduccion}
      >
        Produccion
      </Button>
      <Button
        kind="ghost"
        onMouseOver={changeBackground}
        onMouseOut={changeBackground2}
        onClick={handleCalidad}
      >
        Calidad
      </Button>
      <Button
        kind="ghost"
        onMouseOver={changeBackground}
        onMouseOut={changeBackground2}
        onClick={handlePruebas}
      >
        Pruebas
      </Button>
      <Button
        kind="ghost"
        onMouseOver={changeBackground}
        onMouseOut={changeBackground2}
        onClick={handleManufactura}
      >
        Manufactura
      </Button>
      <Button
        kind="ghost"
        onMouseOver={changeBackground}
        onMouseOut={changeBackground2}
        // onClick= {handleBar}
      >
        Estatus
      </Button>
      <br />
      <br />

      <ContentSwitcher
        selectedIndex={0}
        onChange={(x) => {
          console.log(x.text);
          {
            x.text === "Produccion" ? handleProduccion() : "none";
          }
          {
            x.text === "Calidad" ? handleCalidad() : "none";
          }
          {
            x.text === "Pruebas" ? handlePruebas() : "none";
          }
          {
            x.text === "Tecnical Test" ? handleManufactura() : "none";
          }
          {
            x.text === "Estatus" ? handleEstatus() : "none";
          }
        }}
      >
        <Switch name="one" text="Produccion" />
        <Switch name="two" text="Calidad" />
        <Switch name="three" text="Pruebas" />
        <Switch name="four" text="Tecnical Test" />
        <Switch name="five" text="Estatus" />
      </ContentSwitcher>
      {/* <h1 style={{ color: "black" }}>{state}</h1> */}
      {/* <div>{renderCondition()}</div> */}
      <h4 style={{ color: "blue" }}>{nombre}</h4>
      <hr style={{ border: "transparent" }} />
    </div>
  );
}

export default About;
