import React, { useState } from "react";
import "../../../../public/calidad.css";
import { Button } from "carbon-components-react";
import { Form } from "carbon-components-react";
import TablaEstatus from "./Elementos/TableFilter";
import { TextArea } from "carbon-components-react";
import { GroupedBarChart } from "@carbon/charts-react";
import "@carbon/charts/styles.css";
import "./carbon-components.css";

function Estatus() {
  // Props Row & header for Table
  const state = {
    data: [
      {
        group: "Dataset 1",
        key: "Qty",
        value: 65000
      },
      {
        group: "Dataset 1",
        key: "More",
        value: 29123
      },
      {
        group: "Dataset 1",
        key: "Sold",
        value: 35213
      },
      {
        group: "Dataset 1",
        key: "Restocking",
        value: 51213
      },
      {
        group: "Dataset 1",
        key: "Misc",
        value: 16932
      },
      {
        group: "Dataset 2",
        key: "Qty",
        value: 32432
      },
      {
        group: "Dataset 2",
        key: "More",
        value: 21312
      },
      {
        group: "Dataset 2",
        key: "Sold",
        value: 56456
      },
      {
        group: "Dataset 2",
        key: "Restocking",
        value: 21312
      },
      {
        group: "Dataset 2",
        key: "Misc",
        value: 34234
      },
      {
        group: "Dataset 3",
        key: "Qty",
        value: 12312
      },
      {
        group: "Dataset 3",
        key: "More",
        value: 23232
      },
      {
        group: "Dataset 3",
        key: "Sold",
        value: 34232
      },
      {
        group: "Dataset 3",
        key: "Restocking",
        value: 12312
      },
      {
        group: "Dataset 3",
        key: "Misc",
        value: 34234
      },
      {
        group: "Dataset 4",
        key: "Qty",
        value: 32423
      },
      {
        group: "Dataset 4",
        key: "More",
        value: 21313
      },
      {
        group: "Dataset 4",
        key: "Sold",
        value: 64353
      },
      {
        group: "Dataset 4",
        key: "Restocking",
        value: 24134
      },
      {
        group: "Dataset 4",
        key: "Misc",
        value: 24134
      }
    ],
    options: {
      title: "Vertical grouped bar (discrete)",
      axes: {
        left: {
          mapsTo: "value"
        },
        bottom: {
          scaleType: "labels",
          mapsTo: "key"
        }
      },
      height: "400px"
    }
  };

  return (
    <div id="Estatus" class="tabcontent" style={{ color: "blue" }}>
      <h1>Estatus</h1>
      <hr />
      <Form>
        <div style={{ display: "flex", margin: "0 10px", width: "500px" }}>
          <label style={{ fontSize: "16px", margin: "50px 0 0px 50px" }}>
            Sistemas I/O
          </label>
          <GroupedBarChart
            data={state.data}
            options={state.options}
          ></GroupedBarChart>
        </div>
        <br />
        <hr />

        <TablaEstatus />
        <label style={{ fontSize: "16px", margin: "50px 0 0px 50px" }}>
          Comentarios TT.
        </label>
        <TextArea
          style={{ margin: "25px 0 25px 25px" }}
          cols={40}
          helperText="Optional helper text here; if message is more than one line text should wrap (~100 character count maximum)"
          id="ComentariosTextArea"
          invalidText="Invalid error message."
          // labelText="Comentarios"
          placeholder="cometarios de TT"
          rows={6}
        />
        <Button kind="primary" tabIndex={0} type="submit">
          Guardar
        </Button>
      </Form>
    </div>
  );
}

export default Estatus;
